DROP TABLE IF EXISTS `ptzhaopin`;
CREATE TABLE `ptzhaopin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '招聘题目',
  `name` varchar(255) DEFAULT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `email` varchar(30) DEFAULT NULL,
  `chuanzhen` varchar(20) DEFAULT NULL,
  `shenfe` varchar(20) DEFAULT NULL COMMENT '身份，拼错了，不改了',
  `xingzhi` varchar(50) DEFAULT NULL,
  `hangye` varchar(50) DEFAULT NULL,
  `address` varchar(255) DEFAULT NULL,
  `homeurl` varchar(50) DEFAULT NULL,
  `zpaddr` varchar(255) DEFAULT NULL COMMENT '招聘地点',
  `starttime` datetime DEFAULT NULL COMMENT '开始时间',
  `endtime` datetime DEFAULT NULL COMMENT '结束时间',
  `dwdescr` text COMMENT '单位简介',
  `gangwei` varchar(255) DEFAULT NULL,
  `xueli` varchar(50) DEFAULT NULL,
  `zhuanye` varchar(255) DEFAULT NULL,
  `nums` varchar(10) DEFAULT NULL,
  `gangweidescr` text COMMENT '岗位描述',
  `addtime` datetime DEFAULT NULL COMMENT '添加时间',
  `looksnum` int(11) DEFAULT NULL COMMENT '浏览量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

