DROP TABLE IF EXISTS `student`;
CREATE TABLE `student` (
  `stuid` varchar(25) NOT NULL,
  `name` varchar(25) DEFAULT NULL,
  `sex` varchar(10) DEFAULT '男' COMMENT '性别',
  `classid` int(11) DEFAULT NULL COMMENT '班级id',
  `phone` varchar(15) DEFAULT NULL COMMENT '手机',
  `idcard` varchar(20) DEFAULT NULL COMMENT '身份证',
  `schooltime` datetime DEFAULT NULL COMMENT '入学年月',
  `qqid` varchar(20) DEFAULT NULL COMMENT 'qq号',
  `dirtype` varchar(50) DEFAULT '在校' COMMENT '去向类别',
  `pwd` varchar(255) DEFAULT NULL,
  `reserved1` varchar(255) DEFAULT NULL,
  `reserved2` varchar(255) DEFAULT NULL,
  `dirdetail` varchar(255) DEFAULT NULL COMMENT '去向详情',
  PRIMARY KEY (`stuid`),
  KEY `classid` (`classid`),
  CONSTRAINT `student_ibfk_1` FOREIGN KEY (`classid`) REFERENCES `classroom` (`classid`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

