DROP TABLE IF EXISTS `teacher`;
CREATE TABLE `teacher` (
  `teaid` varchar(25) NOT NULL,
  `name` varchar(25) DEFAULT NULL,
  `sex` varchar(10) DEFAULT '男' COMMENT '性别',
  `academyid` int(11) DEFAULT NULL COMMENT '学院id',
  `level` varchar(50) DEFAULT NULL COMMENT '职称',
  `phone` varchar(15) DEFAULT NULL,
  `idcard` varchar(20) DEFAULT NULL COMMENT '身份证',
  `schooltime` datetime DEFAULT NULL COMMENT '入职时间',
  `qqid` varchar(20) DEFAULT NULL,
  `direction` varchar(50) DEFAULT NULL COMMENT '研究方向',
  `pwd` varchar(255) DEFAULT NULL COMMENT '密码',
  `roletype` varchar(1) DEFAULT '0' COMMENT '角色类型，0普通知道老师，1院系管理员',
  `reserved1` varchar(255) DEFAULT NULL,
  `reserved2` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`teaid`),
  KEY `academyid` (`academyid`),
  CONSTRAINT `teacher_ibfk_1` FOREIGN KEY (`academyid`) REFERENCES `academy` (`academyid`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

